import {createStore} from "vuex"
const ethers = require('ethers');
const {ethereum} = window;

import {USER_PROFILE_ABI} from "@/contracts/UserProfile.abi.js"
import {SKILL_CONFIRMATION_ABI} from "@/contracts/SkillConfirmation.abi.js"
import {SKILL_RATING_ABI} from "@/contracts/SkillRating.abi.js"
// import {TRUST_NETWORK_ABI} from "@/contracts/TrustNetwork.abi.js"


const skillConfirmationAddress = ""
const userProfileAddress = ""
const skillRatingAddress = ""
// const trustNetworkAddress = ""

export default createStore({
  state:{
    provider:{},
    chainId:{},
    signer:{},

    UserProfileContract: {},
    SkillConfirmationContract: {},
    SkillRatingContract: {},
    TrusNetworkContract: {},


    userProfile: "",
    userAddress:"",

    skillRequests: [],
    skillRatings: {},
  },

  getters:{
  },

  mutations:{
  },

  actions:{
    async connectWallet({state}) {
      if (typeof window.ethereum !== 'undefined') {
          console.log("Etherium client installed!");
          if (ethereum.isMetaMask === true) {
              console.log("MetaMask connected!");
              if (ethereum.isConnected() !== true) {
                  console.log("MetaMask is not connected!");
                  await ethereum.enable();
              }
              console.log("MetaMask connected");
          } else {
              alert ("Metamask is not installed!")
          }
      } else {
          alert ("Ethereum client is not installed!")
      }

      state.provider = new ethers.providers.Web3Provider(ethereum);
      state.signer = await state.provider.getSigner();
      state.userAddress = await state.signer.getAddress()

      state.chainId = await window.ethereum.request({ method: "eth_chainId" });

      state.UserProfileContract = new ethers.Contract(userProfileAddress, USER_PROFILE_ABI, state.signer);
      state.SkillConfirmationContract = new ethers.Contract(skillConfirmationAddress, SKILL_CONFIRMATION_ABI, state.signer);
      state.SkillRatingContract = new ethers.Contract(skillRatingAddress, SKILL_RATING_ABI, state.signer);
      //state.TrusNetworkContract = new ethers.Contract(trustNetworkAddress, TRUST_NETWORK_ABI, state.signer);

      console.log("chainId: ", state.chainId);

      ethereum.on("accountsChanged", async () => {
        state.chainId = await window.ethereum.request({ method: "eth_chainId" });
        state.signer = await state.provider.getSigner();
        state.userAddress = await state.signer.getAddress()        
        console.log(`Accounts changed to ${state.address}`);
      })

      ethereum.on("chainChanged", async () => {
          state.provider = new ethers.providers.Web3Provider(ethereum);
          state.chainId = await window.ethereum.request({ method: "eth_chainId" });
          state.signer = await state.provider.getSigner();
          state.userAddress = await state.signer.getAddress()
          state.UserProfileContract = new ethers.Contract(userProfileAddress, USER_PROFILE_ABI, state.signer);
          state.SkillConfirmationContract = new ethers.Contract(skillConfirmationAddress, SKILL_CONFIRMATION_ABI, state.signer);
          state.SkillRatingContract = new ethers.Contract(skillRatingAddress, SKILL_RATING_ABI, state.signer);
          //state.TrusNetworkContract = new ethers.Contract(trustNetworkAddress, TRUST_NETWORK_ABI, state.signer);
          console.log("chainId changed to ", state.chainId);
      })
  },
    // Действия для взаимодействия с контрактами Ethereum
      async createProfile({ state }, profileData) {
        try {
          const tx = await state.UserProfileContract.createProfile(profileData);
          await tx.wait();
          return true;
        } catch (error) {
          console.error("Error creating profile:", error);
          return false;
        }
      },
      async requestConfirmation({ state }, { confirmatorAddress, skillName }) {
        try {
          const tx = await state.SkillConfirmationContract.requestConfirmation(confirmatorAddress, skillName);
          await tx.wait();
          // Обновить skillRequests после успешной транзакции
          const requests = await state.SkillConfirmationContract.getSkillRequestsByAddress(confirmatorAddress);
          state.skillRequests = requests;
          return true;
        } catch (error) {
          console.error("Error requesting confirmation:", error);
          return false;
        }
      },
      async confirmSkill({ state }, requestIndex) {
        try {
          const tx = await state.SkillConfirmationContract.confirmSkill(requestIndex);
          await tx.wait();
          // Обновить skillRequests после успешной транзакции
          const requests = await state.SkillConfirmationContract.getSkillRequestsByAddress(state.userAddress);
          state.skillRequests = requests;
          return true;
        } catch (error) {
          console.error("Error confirming skill:", error);
          return false;
        }
      },
      async rateSkill({ state }, { skillName, rating, comment }) {
        try {
          const tx = await state.SkillRatingContract.rateSkill(skillName, rating, comment);
          await tx.wait();
          // Обновить skillRatings после успешной транзакции
          const ratings = await state.SkillRatingContract.getSkillRatingsByName(skillName);
          state.skillRatings = ratings;
          return true;
        } catch (error) {
          console.error("Error rating skill:", error);
          return false;
        }
      },
      async fetchUserProfile({ state }, address) {
        try {
          const profile = await state.UserProfileContract.profiles(address);
          state.userProfile = profile;
        } catch (error) {
          console.error("Error fetching user profile:", error);
        }
      },
      async fetchSkillRequests({ state }, address) {
        try {
          const requests = await state.SkillConfirmationContract.getSkillRequestsByAddress(address);
          state.skillRequests = requests;
        } catch (error) {
          console.error("Error fetching skill requests:", error);
        }
      },
      async fetchSkillRatings({ state }, skillName) {
        try {
          const ratings = await state.SkillRatingContract.getSkillRatingsByName(skillName);
          state.ratings = ratings;
        } catch (error) {
          console.error("Error fetching skill ratings:", error);
        }
      },
  },

  modules:{

  }
})
