            console.log("Metamask connected!")
           }
           else {
            console.log("Metamask is not connected!")
            await window.ethereum.enable()
            console.log("Metamask connected!")
           }

           window.ethereum.request({method: "eth_requestAccounts"}).then(accounts => {
            state.wallet.address = accounts[0]
          })

            state.web3Wallet = new Web3(window.ethereum)
            //контракты
            state.UserProfileContract = new state.web3Wallet.eth.Contract(USER_PROFILE_ABI, userProfileAddress)
            state.SkillConfirmationContract = new state.web3Wallet.eth.Contract(SKILL_CONFIRMATION_ABI, skillConfirmationAddress)
            state.SkillRatingContract = new state.web3Wallet.eth.Contract(SKILL_RATING_ABI, skillRatingAddress)
            state.TrusNetworkContract = new state.web3Wallet.eth.Contract(TRUST_NETWORK_ABI, trustNetworkAddress)


            state.wallet.chainId = await state.web3Wallet.eth.net.getId()
            state.wallet.chain = await state.web3Wallet.eth.net.getNetworkType()

          window.ethereum.on('accountsChanged', (accounts) =>{
            state.wallet.address = accounts[0]
          })

          window.ethereum.on('chainChanged', async () =>{
            state.web3Wallet = new Web3(window.ethereum)
            state.wallet.chainId = await state.web3Wallet.eth.net.getId()
            state.wallet.chain = await state.web3Wallet.eth.net.getNetworkType()
          })
         }         
       }
        else{
        alert("Ethereum client is not installed!")
        }
    },
