import { createStore } from "vuex";
const Web3 = require('web3');

import { USER_PROFILE_ABI } from "@/contracts/UserProfile.abi.js";

const userProfileAddress = "0xcE8DB739F55655e09dC19fE8aB9E41e037417520";
let UserProfileContract;

export default createStore({
  state: {
    web3: {},
    userProfile: {},
    userAddress: "",
    skillRequests: [],
    userProfiles: []
  },

  getters: {},

  mutations: {},

  actions: {
    async connectWallet({ state }) {
      if (typeof window.ethereum !== 'undefined') {
        console.log("Ethereum client installed!");
        if (window.ethereum.isMetaMask === true) {
          console.log("MetaMask connected!");
          if (window.ethereum.isConnected() !== true) {
            console.log("MetaMask is not connected!");
            await window.ethereum.enable();
          }
          console.log("MetaMask connected");
          state.web3 = new Web3(window.ethereum);
          state.userAddress = (await state.web3.eth.getAccounts())[0];

          const chainId = await state.web3.eth.getChainId();
          UserProfileContract = new state.web3.eth.Contract(USER_PROFILE_ABI, userProfileAddress);

          console.log("chainId: ", chainId);

          window.ethereum.on("accountsChanged", async () => {
            state.userAddress = (await state.web3.eth.getAccounts())[0];
            console.log(`Accounts changed to ${state.address}`);
          });

          window.ethereum.on("chainChanged", async () => {
            const newChainId = await state.web3.eth.getChainId();
            state.chainId = newChainId;
            UserProfileContract = new state.web3.eth.Contract(USER_PROFILE_ABI, userProfileAddress);

            console.log("chainId changed to ", state.chainId);
          });
        } else {
          alert("Metamask is not installed!");
        }
      } else {
        alert("Ethereum client is not installed!");
      }
    },

    // Actions to interact with Ethereum contracts
    async createProfile({ state }, { name, contactInfo, socialLinks }) {
      try {
        const accounts = await state.web3.eth.getAccounts();
        const tx = await UserProfileContract.methods.createProfile(name, contactInfo, socialLinks).send({ from: accounts[0] });
        await tx.wait()
        return true;
      } catch (error) {
        console.error("Error creating profile:", error);
        return false;
      }
    },

    async rateSkill({ state }, { skillName, rating, comment, targetUser }) {
      try {
        const accounts = await state.web3.eth.getAccounts();
        const tx = await UserProfileContract.methods.rateSkill(skillName, rating, comment, targetUser).send({ from: accounts[0] });
        await tx.wait()

        return true;
      } catch (error) {
        console.error("Error rating skill:", error);
        return false;
      }
    },

    async fetchUserProfile({ state }, address) {
      try {
        const profile = await UserProfileContract.methods.profiles(address).call();
        state.userProfile = profile;
      } catch (error) {
        console.error("Error fetching user profile:", error);
      }
    },

    async fetchAllUserSkills(address) {
      try {
        const skills = await UserProfileContract.methods.getUserSkills(address).call();
        const uniqueSkills = skills.filter((value, index) => skills.indexOf(value) === index);
        return uniqueSkills;
      } catch (error) {
        console.error("Error fetching skills:", error);
      }
    },

    async fetchAllUserProfiles({ state }) {
      try {
        const users = await UserProfileContract.methods.getAllUserProfiles().call();
        state.userProfiles = users;
      } catch (error) {
        console.error("Error fetching user profiles:", error);
      }
    },

    async fetchAllUserRatings({ state }, address) {
      try {
        const ratings = await UserProfileContract.methods.getUserSkillRatings(address).call();
        return ratings;
      } catch (error) {
        console.error("Error fetching ratings:", error);
      }
    },

    async getUserSkillRatingsAverage({ state }, userProfileAddress) {
      try {
        const ratings = await UserProfileContract.methods.getUserSkillRatings(userProfileAddress).call();
        let totalRating = 0;
        let ratingCount = 0;

        for (const rating of ratings) {
          totalRating += parseInt(rating.rating);
          ratingCount++;
        }

        const averageRating = ratingCount > 0 ? totalRating / ratingCount : 0;

        return averageRating;
      } catch (error) {
        console.error("Error fetching user skill ratings:", error);
        return 0;
      }
    },
  },

  modules: {},
});

